import React, {useState} from "react";
import {Link} from "react-router-dom";
import axios from "axios";

function  RedeemPage(){

    const endpoint = 'http://127.0.0.1:8000/mkevoucher/'

      const [voucher,setVoucher] = useState({
        redemptions:'',
        created_at:'',
        expires_at:'',
        pin:''
    })

    const handleInput = (e) => {
        setVoucher({...voucher,[e.target.name]:e.target.value})
    }

    function handleSubmit(event){
        event.preventDefault()
        axios.post(endpoint,voucher)
            .then(
                response => console.log(response)
            )
            .catch(
                err => console.log(err)
            )
    }


    return (

        <div className='container d-block center-block justify-content-center'>
            <header>
                <div className=" center text-center">
                    <h1 className="text-center">Redeem Your Voucher</h1>
                </div>
            </header>
            <br/>


            <div className='container'>
                <form className='form '>
                    <div className='row d-flex justify-content-center'>
                        <div className="col-md-2 text-center">
                            <label htmlFor="inputPassword4" className="form-label">Pin</label>
                            <input type="password" className="form-control" id="inputPassword4"/>
                        </div>
                    </div>
                    <br/>


                    <div className="d-flex justify-content-center">
                        <button type="submit" className="btn btn-outline-dark">Redeem</button>

                        <Link to={`/`}>
                            <button type="submit" className="btn btn-outline-dark offset-3">Back</button>
                        </Link>
                    </div>

                </form>
            </div>

        </div>
    )
}

export default RedeemPage;
