import React, {useState} from "react";
import {Link} from "react-router-dom";
import axios from "axios";

function CreatePage(){

    const customStylingDiv = {
        width: '200px'
    }

    const endpoint = 'http://127.0.0.1:8000/mkevoucher/'

    const [voucher,setVoucher] = useState({})

    const handleInput = (e) => {
        setVoucher({...voucher,[e.target.name]:e.target.value})

    }

    function handleSubmit(e){
        e.preventDefault()
        console.log(JSON.stringify(voucher))
        var data = JSON.stringify(voucher)
        axios.post('http://127.0.0.1:8000/mkevoucher/',{data})
            .then(
                response => console.log(response.data)
            )
            .catch(
                err => console.log(err.data)
            )
    }

    // const postNewVoucher = async () => {
    //     const name = 'ReactTestVoucher4'
    //     const email = 'react4@mail.com'
    //     const redemptions = 4
    //     const created_at = new Date()
    //     const expires_at = '2024-02-29'
    //     const pin = "1357"
    //
    //     const body = {name,email,redemptions,created_at,expires_at,pin
    //     }
    //
    //     const response = await axios.post(endpoint,body)
    //     console.log(response)
    //     console.log(response.data)
    //     return response.data
    // }
    //
    // const sendHandler = async () => {
    //     const newData = await postNewVoucher()
    // }

    return(
        <div className='container'>
            <header>
                <div className=" d-flex justify-content-center">
                    <h1 className="text-center">Create Voucher</h1>
                </div>
            </header>
            <br/>
            <br/>
            <div className="container text-center align-items-center" >
                <form className='d-flex flex-column align-items-center' onSubmit={handleSubmit}>
                    <div className='row'  style={customStylingDiv}>
                        <label htmlFor="pin" className="form-label">Pin</label>
                        <input type="text" minLength={4} maxLength={4} onChange={handleInput} className="form-control col-md-2 text-center"
                               name="pin"/>
                        <label htmlFor="expires_at" className="form-label">Expiry Date</label>
                        <input type="date" className="form-control col-md-2 text-center" onChange={handleInput} name="expires_at"/>
                        <label htmlFor="redemptions" className="form-label">Redemptions</label>
                        <input type="number" min={1} className="form-control col-md-2 text-center" onChange={handleInput} name="redemptions"/>
                    </div>
                    <br/>
                    <br/>
                    <div className="col-12">
                        <button className="btn btn-outline-dark">Create Voucher</button>

                        <Link to={`/vadmin`}>
                            <button type="submit" className="btn btn-outline-dark offset-3">Back</button>
                        </Link>
                    </div>

                </form>
            </div>

        </div>

    )

}

export default CreatePage
